import React, { Component } from "react";
import { Link } from "react-router-dom";
import ProjectTaskItem from "./ProjectTask/ProjectTaskItem";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getBacklog } from "../actions/projectTaskActions";

class ProjectBoard extends Component {
  componentDidMount() {
    this.props.getBacklog();
  }

  render() {
    const { project_tasks } = this.props.project_tasks;

    let BoardContent;
    let todoItems = [];
    let inprogressItems = [];
    let doneItems = [];

    const BoardAlgorithm = (project_tasks) => {
      if (project_tasks.length < 1) {
        return (
          <div className="alert alert-info text-center" role="alert">
            No project tasks on board
          </div>
        );
      } else {
        const tasks = project_tasks.map((project_task) => (
          <ProjectTaskItem key={project_task.id} project_task={project_task} />
        ));

        for (let i = 0; i < tasks.length; i++) {
          if (tasks[i].props.project_task.status === "TO_DO") {
            todoItems.push(tasks[i]);
          }
          if (tasks[i].props.project_task.status === "IN_PROGRESS") {
            inprogressItems.push(tasks[i]);
          }
          if (tasks[i].props.project_task.status === "DONE") {
            doneItems.push(tasks[i]);
          }
        }

        return <React.Fragment>
          <div className="container">
            <div className="row">
              <div className="col-md-4">
                <div className="card text-center mb-2">
                  <div className="card-header bg-secondary text-white">
                    <h3>TO DO</h3>
                  </div>
                  {todoItems}
                </div>

                <ProjectTaskItem />
              </div>
              <div className="col-md-4">
                <div className="card text-center mb-2">
                  <div className="card-header bg-primary text-white">
                    <h3>In Progress</h3>
                  </div>
                  {inprogressItems}
                </div>

                <ProjectTaskItem />
              </div>
              <div className="col-md-4">
                <div className="card text-center mb-2">
                  <div className="card-header bg-success text-white">
                    <h3>Done</h3>
                  </div>
                  {doneItems}
                </div>
              </div>
            </div>
          </div>
        </React.Fragment>;
      }
    };

    BoardContent = BoardAlgorithm(project_tasks);

    return (
      <div className="container">
        <Link to="/addProjectTask" className="btn btn-primary mb-3">
          <i className="fas fa-plus-circle"> Create Project Task</i>
        </Link>
        <br />
        <hr />
        {BoardContent}
      </div>
    );
  }
}

ProjectBoard.propTypes = {
  getBacklog: PropTypes.func.isRequired,
  project_tasks: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  project_tasks: state.project_task,
});

export default connect(mapStateToProps, { getBacklog })(ProjectBoard);
