import axios from "axios";
import { GET_ERRORS, GET_PROJECT_TASKS } from "./types";

const backend_url = "http://localhost:8080/api/board";

export const addProjectTask = (project_task, history) => async (dispatch) => {
  try {
    await axios.post(backend_url, project_task);
    history.push("/");
    dispatch({
      type: GET_ERRORS,
      payload: {},
    });
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};

export const getBacklog = () => async (dispatch) => {
  const res = await axios.get("http://localhost:8080/api/board/all");
  dispatch({
    type: GET_PROJECT_TASKS,
    payload: res.data 
  })
};


export const deleteProjectTask = pt_id => async dispatch => {
  if(
    window.confirm(
      `you are about to delete the task with ID: ${pt_id}`
      )
    ){
    await axios.delete(`http://localhost:8080/api/board/${pt_id}`)
     }
};
